const swiper=new Swiper(".swiper",{direction:"vertical",loop:!0,spaceBetween:40,pagination:{el:".swiper-pagination"},autoplay:{delay:5e3},speed:1e3});function accordion(){document.querySelectorAll(".accordion__item-trigger").forEach((e=>{e.addEventListener("click",(()=>{const c=e.parentNode;c.classList.contains("accordion__item-active")||(document.querySelectorAll(".accordion__item").forEach((e=>e.classList.remove("accordion__item-active"))),c.classList.add("accordion__item-active"))}))}))}accordion();